mapper.select:
UPDATE PUBLIC.todo_list

  title = #{payload.title}

WHERE id = #{id}
RETURNING id

UPDATE PUBLIC.todo_list

  title = #{payload.title}


  id = #{specification.id}
  AND title like #{specification.titleLike}

RETURNING id

mapper.insert:
insert into PUBLIC.todo_list_history(todo_list_id, history_sequence_no, todo_list_title) select tl.id, (select coalesce(max(tlh.history_sequence_no) + 1, 0) from PUBLIC.todo_list_history tlh where tlh.todo_list_id = tl.id), tl.title from PUBLIC.todo_list as tl
tl.id in
#{entityId}
