mapper.select:
UPDATE PUBLIC.todo_list
SET
title = #{payload.title},
is_deleted = #{payload.isDeleted},
last_modified_at = #{payload.lastModifiedAt},
last_modified_by = #{payload.lastModifiedBy}

  id = #{specification.id}
  AND title like #{specification.titleLike}

RETURNING id

mapper.insert:
insert into PUBLIC.todo_list_history (todo_list_id, history_sequence_no, todo_list_title, is_deleted, created_at, created_by, last_modified_at, last_modified_by) select tl.id, (select coalesce(max(tlh.history_sequence_no) + 1, 0) from PUBLIC.todo_list_history tlh where tlh.todo_list_id = tl.id), tl.title, tl.is_deleted, tl.created_at, tl.created_by, tl.last_modified_at, tl.last_modified_by from PUBLIC.todo_list as tl
tl.id in
#{entityId}
